import React, { useState, useRef } from 'react';
import axios from 'axios';
import { FaCloudUploadAlt, FaCheckCircle, FaExclamationCircle } from 'react-icons/fa';

const BatchUpload = ({ userToken, onUploadSuccess }) => {
    const [selectedFiles, setSelectedFiles] = useState(null);
    const [uploading, setUploading] = useState(false);
    const [progress, setProgress] = useState(0); // ðŸ‘ˆ Track upload percentage
    const [message, setMessage] = useState(null);
    const fileInputRef = useRef();

    const handleFileSelect = (e) => {
        setSelectedFiles(e.target.files);
        setMessage(null);
        setProgress(0);
    };

    const handleUpload = async () => {
        if (!selectedFiles || selectedFiles.length === 0) return;

        const formData = new FormData();
        for (let i = 0; i < selectedFiles.length; i++) {
            formData.append('files', selectedFiles[i]);
        }

        try {
            setUploading(true);
            setMessage(null);
            setProgress(0);

            await axios.post('http://localhost:5000/api/files', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `Bearer ${userToken}`
                },
                // ðŸ‘‡ RESTORED: Progress calculation
                onUploadProgress: (progressEvent) => {
                    const percentCompleted = Math.round(
                        (progressEvent.loaded * 100) / progressEvent.total
                    );
                    setProgress(percentCompleted);
                }
            });

            setMessage({ type: 'success', text: 'Upload successful!' });
            setSelectedFiles(null);
            if (fileInputRef.current) fileInputRef.current.value = '';
            if (onUploadSuccess) onUploadSuccess();

        } catch (error) {
            console.error("Upload error:", error);
            setMessage({ type: 'error', text: 'Upload Failed. Please try again.' });
        } finally {
            setUploading(false);
        }
    };

    return (
        <div className="bg-slate-800 p-8 rounded-xl border border-slate-700 shadow-xl max-w-2xl mx-auto text-center">
            <div className="flex flex-col items-center mb-6">
                <FaCloudUploadAlt className="text-cyan-400 text-5xl mb-4 animate-bounce" />
                <h2 className="text-2xl font-bold text-white">Upload Files</h2>
            </div>

            <div className="relative group">
                <input
                    type="file" multiple onChange={handleFileSelect} ref={fileInputRef}
                    className="absolute inset-0 w-full h-full opacity-0 cursor-pointer z-10"
                />
                <div className="bg-slate-900 border-2 border-dashed border-slate-600 group-hover:border-cyan-500 rounded-lg p-4 flex items-center justify-between transition-colors">
                    <span className="text-slate-400">
                        {selectedFiles ? `${selectedFiles.length} files selected` : "Choose files or drag & drop"}
                    </span>
                    <span className="bg-cyan-600 text-white text-xs font-bold px-3 py-1 rounded uppercase">Browse</span>
                </div>
            </div>

            {/* ðŸ‘‡ RESTORED: Visual Progress Bar */}
            {uploading && (
                <div className="mt-6">
                    <div className="flex justify-between text-xs text-cyan-400 mb-1 font-mono">
                        <span>UPLOADING_DATA...</span>
                        <span>{progress}%</span>
                    </div>
                    <div className="w-full bg-slate-900 rounded-full h-2 overflow-hidden border border-slate-700">
                        <div
                            className="bg-gradient-to-r from-cyan-500 to-blue-500 h-full transition-all duration-300"
                            style={{ width: `${progress}%` }}
                        />
                    </div>
                </div>
            )}

            <button
                onClick={handleUpload}
                disabled={!selectedFiles || uploading}
                className={`w-full mt-6 py-3 rounded-lg font-bold text-white transition-all ${!selectedFiles || uploading
                        ? 'bg-slate-700 text-slate-500'
                        : 'bg-gradient-to-r from-cyan-600 to-blue-600 hover:shadow-cyan-500/30 shadow-lg'
                    }`}
            >
                {uploading ? 'UPLOADING...' : 'INITIATE UPLOAD'}
            </button>

            {message && (
                <div className={`mt-4 p-3 rounded-lg flex items-center justify-center gap-2 text-sm font-semibold ${message.type === 'success' ? 'bg-green-500/20 text-green-400' : 'bg-red-500/20 text-red-400'
                    }`}>
                    {message.type === 'success' ? <FaCheckCircle /> : <FaExclamationCircle />}
                    {message.text}
                </div>
            )}
        </div>
    );
};

export default BatchUpload;
