import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { FaTrash, FaDownload, FaFileAlt, FaImages, FaVideo, FaFilePdf, FaLayerGroup, FaBoxOpen } from 'react-icons/fa';

const FileList = ({ token, refreshTrigger }) => {
    const [files, setFiles] = useState([]);
    const [filter, setFilter] = useState('all');

    useEffect(() => {
        const fetchFiles = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/files', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFiles(res.data);
            } catch (err) { console.error(err); }
        };
        if (token) fetchFiles();
    }, [token, refreshTrigger]);

    const getFileCategory = (file) => {
        const mime = (file.mimetype || file.contentType || '').toLowerCase();
        const name = (file.originalName || file.filename || '').toLowerCase();
        if (mime.startsWith('image/') || name.match(/\.(jpg|jpeg|png|gif|webp)$/)) return 'image';
        if (mime.startsWith('video/') || name.match(/\.(mp4|mov|avi)$/)) return 'video';
        if (mime.includes('pdf') || name.endsWith('.pdf')) return 'document';
        return 'other';
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Delete asset?")) return;
        try {
            await axios.delete(`http://localhost:5000/api/files/${id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setFiles(files.filter(f => f._id !== id));
        } catch (e) { alert("Delete failed"); }
    };

    const filteredFiles = files.filter(f => filter === 'all' || getFileCategory(f) === filter);

    return (
        <div>
            <div className="flex gap-2 mb-8 overflow-x-auto pb-2 scrollbar-hide">
                {['all', 'image', 'video', 'document', 'other'].map(t => (
                    <button key={t} onClick={() => setFilter(t)} className={`px-5 py-2 rounded-lg text-xs font-bold border transition-all ${filter === t ? 'bg-cyan-600 border-cyan-400' : 'bg-slate-800 border-slate-700 text-slate-400'}`}>
                        {t.toUpperCase()}
                    </button>
                ))}
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                {filteredFiles.map(file => {
                    const isImage = getFileCategory(file) === 'image';
                    return (
                        <div key={file._id} className="group bg-slate-800 rounded-xl overflow-hidden border border-slate-700 hover:border-cyan-500 shadow-xl transition-all">
                            <div className="h-40 bg-slate-950 flex items-center justify-center relative overflow-hidden">
                                {isImage ? (
                                    <img src={`http://localhost:5000/api/files/${file._id}`} className="w-full h-full object-cover transition-transform group-hover:scale-110" alt="asset" />
                                ) : <FaFileAlt className="text-4xl text-slate-700" />}

                                <div className="absolute inset-0 bg-black/60 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center gap-4">
                                    <button onClick={() => handleDelete(file._id)} className="p-2 bg-red-600 rounded-full text-white hover:bg-red-500"><FaTrash /></button>
                                    <a href={`http://localhost:5000/api/files/${file._id}`} download className="p-2 bg-cyan-600 rounded-full text-white hover:bg-cyan-500"><FaDownload /></a>
                                </div>
                            </div>
                            <div className="p-3 bg-slate-800/80 border-t border-slate-700">
                                <p className="text-xs font-medium truncate text-slate-300">{file.originalName || file.filename}</p>
                                <p className="text-[10px] text-slate-500 mt-1 uppercase font-mono">{file.contentType || file.mimetype || 'UNKNOWN'}</p>
                            </div>
                        </div>
                    );
                })}
            </div>
        </div>
    );
};

export default FileList;
