import React, { useState } from 'react';
import api from '../services/api';
import { toast } from 'react-toastify';
import { FaCloudUploadAlt, FaCheckCircle, FaSpinner } from 'react-icons/fa';
import { motion, AnimatePresence } from 'framer-motion';

const FileUpload = () => {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [uploadedFile, setUploadedFile] = useState(null);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
        setUploadedFile(null); // Reset preview on new selection
    };

    const handleUpload = async (e) => {
        e.preventDefault();
        if (!file) return toast.warning("‚ö†Ô∏è Select a file protocol initiated.");

        setLoading(true);
        const formData = new FormData();
        formData.append('file', file);

        try {
            const res = await api.post('/files/upload', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });

            toast.success("üöÄ Upload Complete!");
            setUploadedFile(res.data.file);
            setFile(null);
            // Optional: Auto-reload page or trigger refresh context here
            // For now, user can manually refresh to see it in gallery
        } catch (err) {
            console.error(err);
            toast.error("‚ùå Critical Failure: Upload Aborted");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="w-full max-w-2xl mx-auto">
            <div className="relative group">

                {/* Glowing Background Effect */}
                <div className="absolute -inset-1 bg-gradient-to-r from-cyan-600 to-blue-600 rounded-2xl blur opacity-25 group-hover:opacity-75 transition duration-1000"></div>

                <div className="relative bg-slate-800 border border-slate-700 p-8 rounded-2xl shadow-2xl">
                    <div className="text-center">
                        <motion.div
                            animate={{ y: [0, -10, 0] }}
                            transition={{ repeat: Infinity, duration: 3 }}
                            className="inline-block mb-4"
                        >
                            <FaCloudUploadAlt className="text-6xl text-cyan-400" />
                        </motion.div>

                        <h2 className="text-2xl font-bold text-white mb-2">Upload Data Packet</h2>
                        <p className="text-slate-400 mb-6">Drag and drop or select files to transmit to the mainframe.</p>

                        <form onSubmit={handleUpload} className="space-y-4">
                            <input
                                type="file"
                                onChange={handleFileChange}
                                className="block w-full text-sm text-slate-300
                  file:mr-4 file:py-2 file:px-4
                  file:rounded-full file:border-0
                  file:text-sm file:font-semibold
                  file:bg-cyan-900 file:text-cyan-300
                  hover:file:bg-cyan-800 cursor-pointer"
                            />

                            <button
                                type="submit"
                                disabled={loading}
                                className={`w-full py-3 px-6 rounded-lg font-bold uppercase tracking-wider transition-all duration-300 
                  ${loading
                                        ? 'bg-slate-700 cursor-not-allowed'
                                        : 'bg-gradient-to-r from-cyan-500 to-blue-500 hover:from-cyan-400 hover:to-blue-400 text-white shadow-lg shadow-cyan-500/50'
                                    }`}
                            >
                                {loading ? <span className="flex items-center justify-center gap-2"><FaSpinner className="animate-spin" /> Transmitting...</span> : 'Initiate Upload'}
                            </button>
                        </form>
                    </div>

                    {/* Success Preview */}
                    <AnimatePresence>
                        {uploadedFile && (
                            <motion.div
                                initial={{ height: 0, opacity: 0 }}
                                animate={{ height: 'auto', opacity: 1 }}
                                exit={{ height: 0, opacity: 0 }}
                                className="mt-6 p-4 bg-green-500/10 border border-green-500/30 rounded-lg flex items-center gap-4"
                            >
                                <FaCheckCircle className="text-green-400 text-2xl" />
                                <div>
                                    <h4 className="font-bold text-green-300">Transmission Successful</h4>
                                    <p className="text-sm text-green-400/80">File ID: {uploadedFile._id.substring(0, 10)}...</p>
                                </div>
                            </motion.div>
                        )}
                    </AnimatePresence>
                </div>
            </div>
        </div>
    );
};

export default FileUpload;
