import React, { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import FileUpload from '../components/FileUpload'; // We will build this in Step 3

const Dashboard = () => {
    const { user, logout } = useContext(AuthContext);

    return (
        <div style={{ padding: '40px', fontFamily: 'Arial, sans-serif' }}>
            <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '40px' }}>
                <h1>ðŸš€ My Cloud Drive</h1>
                <div>
                    <span style={{ marginRight: '15px' }}>Hello, <strong>{user?.username || 'User'}</strong>!</span>
                    <button
                        onClick={logout}
                        style={{ padding: '8px 16px', background: '#ff4d4d', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer' }}
                    >
                        Logout
                    </button>
                </div>
            </header>

            <main>
                <FileUpload />
            </main>
        </div>
    );
};

export default Dashboard;
