import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom'; // ðŸ‘ˆ Needed for Profile Link
import { AuthContext } from '../context/AuthContext';
import BatchUpload from '../components/BatchUpload';
import FileList from '../components/FileList';
import { motion } from 'framer-motion';
import { FaSignOutAlt, FaRocket, FaUserCircle } from 'react-icons/fa';

const Dashboard = () => {
    const { user, token: ctxToken, logout } = useContext(AuthContext);

    // Safety: If context is slow, grab token directly from storage
    const token = ctxToken || localStorage.getItem('token');

    // State to trigger auto-refresh of the gallery after upload
    const [refreshKey, setRefreshKey] = useState(0);

    const handleUploadSuccess = () => {
        setRefreshKey(prev => prev + 1);
    };

    return (
        <div className="min-h-screen bg-slate-900 text-white font-sans selection:bg-cyan-500 selection:text-black">

            {/* 1. Header Section */}
            <motion.header
                initial={{ y: -50, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                className="flex justify-between items-center p-6 border-b border-slate-700 bg-slate-800/50 backdrop-blur-md sticky top-0 z-50 shadow-lg shadow-cyan-500/10"
            >
                {/* Logo Area */}
                <div className="flex items-center gap-3">
                    <FaRocket className="text-cyan-400 text-3xl animate-pulse" />
                    <h1 className="text-2xl font-bold bg-gradient-to-r from-cyan-400 to-blue-500 bg-clip-text text-transparent">
                        AETHER CLOUD
                    </h1>
                </div>

                {/* User Controls */}
                <div className="flex items-center gap-6">

                    {/* Profile Link (Clickable Name & Avatar) */}
                    <Link to="/profile" className="hidden md:flex items-center gap-3 group">
                        <span className="text-slate-300 group-hover:text-white transition text-sm">
                            Operator: <span className="text-cyan-300 font-mono font-bold">{user?.name || 'Unknown'}</span>
                        </span>

                        {/* Avatar Display */}
<div className="w-10 h-10 rounded-full overflow-hidden border-2 border-slate-600 group-hover:border-cyan-400 transition-colors bg-slate-800">
    {user?.avatar ? (
        <img 
            // ðŸ‘‡ FIX 1: Add cache buster (?t=...) to force reload
            src={`http://localhost:5000/api/files/${user.avatar}?t=${new Date().getTime()}`} 
            alt="Avatar" 
            
            // ðŸ‘‡ FIX 2: Security attributes to prevent blocking
            crossOrigin="anonymous"
            referrerPolicy="no-referrer"
            
            className="w-full h-full object-cover" 
            
            // ðŸ‘‡ FIX 3: Fallback to icon if image fails
            onError={(e) => {
                e.target.style.display = 'none'; // Hide broken image
                e.target.parentNode.innerHTML = '<svg stroke="currentColor" fill="currentColor" stroke-width="0" viewBox="0 0 496 512" height="1em" width="1em" xmlns="http://www.w3.org/2000/svg" style="color: rgb(148, 163, 184); font-size: 1.5rem; margin: auto;"><path d="M248 8C111 8 0 119 0 256s111 248 248 248 248-111 248-248S385 8 248 8zm0 96c48.6 0 88 39.4 88 88s-39.4 88-88 88-88-39.4-88-88 39.4-88 88-88zm0 344c-58.7 0-111.3-26.6-146.5-68.2 18.8-35.4 55.6-59.8 98.5-59.8 2.4 0 4.8.4 7.1 1.1 13 4.2 26.6 6.9 40.9 6.9 14.3 0 28-2.7 40.9-6.9 2.3-.7 4.7-1.1 7.1-1.1 42.9 0 79.7 24.4 98.5 59.8C359.3 421.4 306.7 448 248 448z"></path></svg>';
            }}
        />
    ) : (
        <div className="w-full h-full flex items-center justify-center text-slate-500">
            <FaUserCircle className="text-2xl"/>
        </div>
    )}
</div>
                    </Link>

                    {/* Logout Button */}
                    <button
                        onClick={logout}
                        className="flex items-center gap-2 px-4 py-2 bg-red-500/20 text-red-400 border border-red-500/50 rounded-lg hover:bg-red-500 hover:text-white transition-all duration-300"
                    >
                        <FaSignOutAlt /> <span className="hidden sm:inline">Logout</span>
                    </button>
                </div>
            </motion.header>

            {/* 2. Main Content Area */}
            <main className="max-w-7xl mx-auto p-6 space-y-12">

                {/* Upload Section (Fade In) */}
                <motion.section
                    initial={{ opacity: 0, scale: 0.95 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ delay: 0.2 }}
                >
                    <BatchUpload
                        userToken={token}
                        onUploadSuccess={handleUploadSuccess}
                    />
                </motion.section>

                {/* Divider */}
                <div className="h-px bg-gradient-to-r from-transparent via-slate-600 to-transparent" />

                {/* Gallery Section (Slide Up) */}
                <motion.section
                    initial={{ opacity: 0, y: 50 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.4 }}
                >
                    <FileList
                        token={token}
                        refreshTrigger={refreshKey}
                    />
                </motion.section>

            </main>
        </div>
    );
};

export default Dashboard;
