// src/pages/Dashboard.js
import React, { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import BatchUpload from '../components/BatchUpload';

const Dashboard = () => {
    // 1. Get the current user/token from Context
    const { user, token, logout } = useContext(AuthContext); 

    return (
        <div className="dashboard-container" style={{ padding: '20px' }}>
            <header style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '40px' }}>
                <h1>Welcome, {user ? user.name : 'User'}!</h1>
                <button onClick={logout} style={{ background: 'red', color: 'white', padding: '10px' }}>
                    Logout
                </button>
            </header>

            <div className="dashboard-content">
                <h2>Your File Center</h2>
                <p>Upload your videos, images, or documents below.</p>
                
                {/* 2. Drop the BatchUpload here. We pass the token safely! */}
                <div style={{ marginTop: '30px' }}>
                    <BatchUpload userToken={token} />
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
