import React, { useState } from 'react';
import api from '../services/api';
import { toast } from 'react-toastify';
import { FaCloudUploadAlt, FaFile } from 'react-icons/fa'; // Icons

const FileUpload = () => {
    const [file, setFile] = useState(null);
    const [uploadedFile, setUploadedFile] = useState(null);

    const handleFileChange = (e) => {
        setFile(e.target.files[0]);
    };

    const handleUpload = async (e) => {
        e.preventDefault();
        if (!file) return toast.warning("Please select a file first!");

        const formData = new FormData();
        formData.append('file', file);

        try {
            const res = await api.post('/files/upload', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });

            toast.success("File Uploaded Successfully! ðŸŽ‰");
            setUploadedFile(res.data.file); // Save file info to show preview
            setFile(null); // Reset input
        } catch (err) {
            console.error(err);
            toast.error(err.response?.data?.error || "Upload Failed");
        }
    };

    return (
        <div style={{ maxWidth: '600px', margin: '0 auto', textAlign: 'center' }}>
            <div style={{ border: '2px dashed #ccc', padding: '40px', borderRadius: '10px', backgroundColor: '#f9f9f9' }}>
                <FaCloudUploadAlt size={50} color="#007bff" />
                <h3>Upload your files here</h3>

                <form onSubmit={handleUpload} style={{ marginTop: '20px' }}>
                    <input type="file" onChange={handleFileChange} style={{ marginBottom: '10px' }} />
                    <br />
                    <button
                        type="submit"
                        style={{
                            marginTop: '15px',
                            padding: '10px 25px',
                            background: '#007bff',
                            color: 'white',
                            border: 'none',
                            borderRadius: '5px',
                            cursor: 'pointer',
                            fontSize: '16px'
                        }}
                    >
                        Upload Now
                    </button>
                </form>
            </div>

            {/* Preview Section */}
            {uploadedFile && (
                <div style={{ marginTop: '30px', textAlign: 'left', border: '1px solid #ddd', padding: '20px', borderRadius: '8px' }}>
                    <h4>âœ… Latest Upload:</h4>
                    <p><FaFile /> <strong>Name:</strong> {uploadedFile.originalName}</p>
                    <p><strong>Size:</strong> {(uploadedFile.size / 1024).toFixed(2)} KB</p>

                    {uploadedFile.mimetype.startsWith('image') && (
                        <img
                            src={`http://localhost:5000/api/files/${uploadedFile._id}`}
                            alt="Preview"
                            style={{ maxWidth: '100%', marginTop: '10px', borderRadius: '5px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)' }}
                        />
                    )}
                </div>
            )}
        </div>
    );
};

export default FileUpload;
