import React, { createContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import { toast } from 'react-toastify';

// Create the Context
export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    // 1. Check if user is already logged in when app loads
    useEffect(() => {
        const checkUser = async () => {
            const token = localStorage.getItem('token');
            if (token) {
                try {
                    // Verify token with backend (Optional but recommended)
                    // For now, we assume if token exists, user is logged in
                    setUser({ token });
                } catch (error) {
                    localStorage.removeItem('token');
                    setUser(null);
                }
            }
            setLoading(false);
        };
        checkUser();
    }, []);

    // 2. Login Function
    const login = async (email, password) => {
        try {
            const res = await api.post('/auth/login', { email, password });
            localStorage.setItem('token', res.data.token);
            setUser({ token: res.data.token, ...res.data.user });
            toast.success('Login Successful! ðŸš€');
            navigate('/'); // Redirect to Dashboard
        } catch (err) {
            toast.error(err.response?.data?.error || 'Login Failed');
        }
    };

    // 3. Register Function
    const register = async (username, email, password) => {
        try {
            const res = await api.post('/auth/register', { username, email, password });
            localStorage.setItem('token', res.data.token);
            setUser({ token: res.data.token, ...res.data.user });
            toast.success('Welcome aboard! ðŸŽ‰');
            navigate('/');
        } catch (err) {
            toast.error(err.response?.data?.error || 'Registration Failed');
        }
    };

    // 4. Logout Function
    const logout = () => {
        localStorage.removeItem('token');
        setUser(null);
        toast.info('Logged out ðŸ‘‹');
        navigate('/login');
    };

    return (
        <AuthContext.Provider value={{ user, loading, login, register, logout }}>
            {!loading && children}
        </AuthContext.Provider>
    );
};
