import React, { useContext, useState } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import BatchUpload from '../components/BatchUpload';
import FileList from '../components/FileList';
import { motion } from 'framer-motion';
import { FaSignOutAlt, FaRocket, FaUserCircle, FaImage, FaVideo, FaFileAlt, FaThLarge } from 'react-icons/fa';

const Dashboard = () => {
    const { user, token: ctxToken, logout } = useContext(AuthContext);
    const token = ctxToken || localStorage.getItem('token');

    const [refreshKey, setRefreshKey] = useState(0);

    // ðŸ‘‡ RESTORED: Filter State
    const [filter, setFilter] = useState('all');

    const handleUploadSuccess = () => {
        setRefreshKey(prev => prev + 1);
        setFilter('all'); // Reset filter to show new files
    };

    // Filter Buttons Configuration
    const filters = [
        { id: 'all', label: 'All Files', icon: <FaThLarge /> },
        { id: 'image', label: 'Images', icon: <FaImage /> },
        { id: 'video', label: 'Videos', icon: <FaVideo /> },
        { id: 'document', label: 'Documents', icon: <FaFileAlt /> },
    ];

    return (
        <div className="min-h-screen bg-slate-900 text-white font-sans selection:bg-cyan-500 selection:text-black">

            {/* Header */}
            <motion.header
                initial={{ y: -50, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                className="flex justify-between items-center p-6 border-b border-slate-700 bg-slate-800/50 backdrop-blur-md sticky top-0 z-50 shadow-lg shadow-cyan-500/10"
            >
                <div className="flex items-center gap-3">
                    <FaRocket className="text-cyan-400 text-3xl animate-pulse" />
                    <h1 className="text-2xl font-bold bg-gradient-to-r from-cyan-400 to-blue-500 bg-clip-text text-transparent">
                        AETHER CLOUD
                    </h1>
                </div>

                <div className="flex items-center gap-6">
                    <Link to="/profile" className="hidden md:flex items-center gap-3 group">
                        <span className="text-slate-300 group-hover:text-white transition text-sm">
                            Operator: <span className="text-cyan-300 font-mono font-bold">{user?.name || 'Unknown'}</span>
                        </span>

                        {/* Avatar Display (Simple/Stable Version) */}
                        <div className="w-10 h-10 rounded-full overflow-hidden border-2 border-slate-600 group-hover:border-cyan-400 transition-colors bg-slate-800 flex items-center justify-center">
                            {user?.avatar ? (
                                <img
                                    src={`http://localhost:5000/api/files/${user.avatar}`}
                                    alt="Avatar"
                                    className="w-full h-full object-cover"
                                />
                            ) : (
                                <FaUserCircle className="text-2xl text-slate-500" />
                            )}
                        </div>
                    </Link>

                    <button
                        onClick={logout}
                        className="flex items-center gap-2 px-4 py-2 bg-red-500/20 text-red-400 border border-red-500/50 rounded-lg hover:bg-red-500 hover:text-white transition-all duration-300"
                    >
                        <FaSignOutAlt /> <span className="hidden sm:inline">Logout</span>
                    </button>
                </div>
            </motion.header>

            {/* Main Content */}
            <main className="max-w-7xl mx-auto p-6 space-y-8">

                {/* Upload Section */}
                <motion.section
                    initial={{ opacity: 0, scale: 0.95 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ delay: 0.2 }}
                >
                    <BatchUpload userToken={token} onUploadSuccess={handleUploadSuccess} />
                </motion.section>

                <div className="h-px bg-gradient-to-r from-transparent via-slate-600 to-transparent" />

                {/* ðŸ‘‡ RESTORED: Filter Tabs UI */}
                <motion.div
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="flex flex-wrap gap-4 justify-center md:justify-start"
                >
                    {filters.map((f) => (
                        <button
                            key={f.id}
                            onClick={() => setFilter(f.id)}
                            className={`flex items-center gap-2 px-6 py-3 rounded-lg font-semibold transition-all duration-300 border ${filter === f.id
                                    ? 'bg-cyan-600 border-cyan-500 text-white shadow-lg shadow-cyan-500/20 scale-105'
                                    : 'bg-slate-800 border-slate-700 text-slate-400 hover:bg-slate-700 hover:text-white'
                                }`}
                        >
                            {f.icon} {f.label}
                        </button>
                    ))}
                </motion.div>

                {/* File List Section - Now receives "filter" prop */}
                <motion.section
                    initial={{ opacity: 0, y: 50 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.4 }}
                >
                    <FileList token={token} refreshTrigger={refreshKey} filter={filter} />
                </motion.section>
            </main>
        </div>
    );
};

export default Dashboard;
