import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { AnimatePresence, motion } from 'framer-motion';
import {
    FaTrash, FaDownload, FaFileAlt, FaExclamationTriangle,
    FaImages, FaVideo, FaFilePdf, FaLayerGroup, FaBoxOpen, FaTimes
} from 'react-icons/fa';

const FileList = ({ token, refreshTrigger }) => {
    const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [failedImages, setFailedImages] = useState({});
    const [filter, setFilter] = useState('all');
    const [selectedFile, setSelectedFile] = useState(null);

    useEffect(() => {
        const fetchFiles = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/files', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFiles(res.data);
            } catch (error) {
                console.error("Fetch failed", error);
            } finally {
                setLoading(false);
            }
        };
        if (token) fetchFiles();
    }, [token, refreshTrigger]);

    const getFileCategory = (file) => {
        const mime = (file.mimetype || file.contentType || '').toLowerCase();
        const name = (file.originalName || file.filename || '').toLowerCase();
        if (mime.startsWith('image/') || name.match(/\.(jpg|jpeg|png|gif|webp)$/)) return 'image';
        if (mime.startsWith('video/') || name.match(/\.(mp4|mov|avi)$/)) return 'video';
        if (mime.includes('pdf') || name.endsWith('.pdf')) return 'document';
        return 'other';
    };

    const handleDelete = async (id) => {
        if (!window.confirm("Delete this asset?")) return;
        try {
            await axios.delete(`http://localhost:5000/api/files/${id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setFiles(files.filter(f => f._id !== id));
        } catch (e) { alert("Delete failed"); }
    };

    const filteredFiles = files.filter(f => filter === 'all' || getFileCategory(f) === filter);

    if (loading) return <p className="text-center text-slate-500 font-mono">LOADING_SECTORS...</p>;

    return (
        <div>
            <div className="flex flex-col md:flex-row justify-between items-center mb-8 border-b border-slate-700 pb-4 gap-4">
                <h3 className="text-xl font-bold">Gallery Assets</h3>
                <div className="flex gap-2 overflow-x-auto pb-2 w-full md:w-auto">
                    {['all', 'image', 'video', 'document', 'other'].map(t => (
                        <button key={t} onClick={() => setFilter(t)} className={`px-4 py-2 rounded-lg text-xs font-bold flex items-center gap-2 border transition-all ${filter === t ? 'bg-cyan-600 border-cyan-400' : 'bg-slate-800 border-slate-700 text-slate-400'}`}>
                            {t.toUpperCase()}
                        </button>
                    ))}
                </div>
            </div>

            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
                {filteredFiles.map(file => {
                    const category = getFileCategory(file);
                    const isImage = category === 'image';
                    const isBroken = failedImages[file._id];
                    return (
                        <motion.div layout key={file._id} className="group bg-slate-800 rounded-xl overflow-hidden border border-slate-700 hover:border-cyan-500 shadow-xl">
                            <div className="h-40 bg-slate-950 flex items-center justify-center relative cursor-pointer" onClick={() => isImage && !isBroken && setSelectedFile(file)}>
                                {isImage && !isBroken ? (
                                    <img src={`http://localhost:5000/api/files/${file._id}`} className="w-full h-full object-cover" onError={() => setFailedImages(p => ({ ...p, [file._id]: true }))} />
                                ) : <FaFileAlt className="text-4xl text-slate-700" />}
                                <div className="absolute inset-0 bg-slate-900/80 opacity-0 group-hover:opacity-100 transition-all flex items-center justify-center gap-4">
                                    <button onClick={(e) => { e.stopPropagation(); handleDelete(file._id); }} className="p-2 bg-red-600 rounded-full text-white"><FaTrash /></button>
                                    <a href={`http://localhost:5000/api/files/${file._id}`} download className="p-2 bg-cyan-600 rounded-full text-white"><FaDownload /></a>
                                </div>
                            </div>
                            <div className="p-3 bg-slate-800/80">
                                <p className="text-xs font-medium truncate">{file.originalName || file.filename}</p>
                            </div>
                        </motion.div>
                    );
                })}
            </div>

            <AnimatePresence>
                {selectedFile && (
                    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }} exit={{ opacity: 0 }} className="fixed inset-0 z-[100] flex items-center justify-center bg-black/95 p-4" onClick={() => setSelectedFile(null)}>
                        <button className="absolute top-5 right-5 text-white text-3xl"><FaTimes /></button>
                        <motion.img initial={{ scale: 0.9 }} animate={{ scale: 1 }} src={`http://localhost:5000/api/files/${selectedFile._id}`} className="max-w-full max-h-[80vh] rounded-lg shadow-2xl" />
                    </motion.div>
                )}
            </AnimatePresence>
        </div>
    );
};

export default FileList;
