import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import api from '../services/api';
import { toast } from 'react-toastify';
import { FaCloudUploadAlt, FaCheckCircle, FaFileCode } from 'react-icons/fa';
import { motion, AnimatePresence } from 'framer-motion';

const FileUpload = () => {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [progress, setProgress] = useState(0); // ðŸ‘ˆ New State for Progress
    const [uploadedFile, setUploadedFile] = useState(null);

    const onDrop = useCallback((acceptedFiles) => {
        if (acceptedFiles?.length > 0) {
            setFile(acceptedFiles[0]);
            setUploadedFile(null);
            setProgress(0); // Reset progress on new file
        }
    }, []);

    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        multiple: false
    });

    const handleUpload = async (e) => {
        e.preventDefault();
        if (!file) return toast.warning("âš ï¸ No file detected.");

        setLoading(true);
        setProgress(0); // Reset to 0 before starting

        const formData = new FormData();
        formData.append('file', file);

        try {
            const res = await api.post('/files/upload', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },

                // ðŸ‘‡ THE MAGIC PART: Track Upload Speed
                onUploadProgress: (data) => {
                    // Calculate percentage (loaded / total)
                    const percent = Math.round((100 * data.loaded) / data.total);
                    setProgress(percent);
                },
            });

            toast.success("ðŸš€ Transmission Complete!");
            setUploadedFile(res.data.file);
            setFile(null);
        } catch (err) {
            console.error(err);
            toast.error("âŒ Transmission Failed");
        } finally {
            setLoading(false);
            // Optional: Reset progress after a delay if you want
            setTimeout(() => setProgress(0), 1000);
        }
    };

    return (
        <div className="w-full max-w-2xl mx-auto">
            <div className="relative group">

                {/* Background Glow */}
                <div className={`absolute -inset-1 rounded-2xl blur opacity-25 transition duration-1000 
          ${isDragActive ? 'bg-green-500 opacity-75' : 'bg-gradient-to-r from-cyan-600 to-blue-600 group-hover:opacity-75'}`}>
                </div>

                <div className="relative bg-slate-800 border border-slate-700 p-8 rounded-2xl shadow-2xl">
                    <div className="text-center">

                        {/* Drop Zone */}
                        <div
                            {...getRootProps()}
                            className={`border-2 border-dashed rounded-xl p-10 cursor-pointer transition-all duration-300 flex flex-col items-center justify-center gap-4
                ${isDragActive
                                    ? 'border-green-400 bg-green-500/10 scale-105'
                                    : 'border-slate-600 hover:border-cyan-400 hover:bg-slate-700/50'
                                }
              `}
                        >
                            <input {...getInputProps()} />

                            <motion.div
                                animate={isDragActive ? { scale: 1.2 } : { scale: 1 }}
                                className="text-6xl"
                            >
                                {file ? <FaFileCode className="text-yellow-400" /> : <FaCloudUploadAlt className={isDragActive ? "text-green-400" : "text-cyan-400"} />}
                            </motion.div>

                            <div className="space-y-2">
                                {file ? (
                                    <div>
                                        <h3 className="text-xl font-bold text-white">Target Locked</h3>
                                        <p className="text-yellow-400 font-mono text-sm">{file.name}</p>
                                        <p className="text-slate-500 text-xs">{(file.size / 1024).toFixed(2)} KB</p>
                                    </div>
                                ) : (
                                    <div>
                                        <h3 className="text-xl font-bold text-white">
                                            {isDragActive ? "Drop to Initialize" : "Upload Data Packet"}
                                        </h3>
                                        <p className="text-slate-400">Drag & drop files here</p>
                                    </div>
                                )}
                            </div>
                        </div>

                        {/* ðŸ‘‡ PROGRESS BAR SECTION ðŸ‘‡ */}
                        <AnimatePresence>
                            {loading && (
                                <motion.div
                                    initial={{ opacity: 0, height: 0 }}
                                    animate={{ opacity: 1, height: 'auto' }}
                                    exit={{ opacity: 0, height: 0 }}
                                    className="mt-6"
                                >
                                    <div className="flex justify-between text-xs text-cyan-400 mb-1 font-mono">
                                        <span>TRANSMITTING...</span>
                                        <span>{progress}%</span>
                                    </div>

                                    {/* The Bar Background */}
                                    <div className="w-full bg-slate-700 rounded-full h-4 overflow-hidden border border-slate-600">
                                        {/* The Bar Fill (Animated Width) */}
                                        <motion.div
                                            className="h-full bg-gradient-to-r from-cyan-500 to-blue-500 shadow-[0_0_10px_#06b6d4]"
                                            initial={{ width: 0 }}
                                            animate={{ width: `${progress}%` }}
                                            transition={{ duration: 0.1 }}
                                        />
                                    </div>
                                </motion.div>
                            )}
                        </AnimatePresence>

                        {/* Launch Button (Only show if not loading) */}
                        {!loading && file && (
                            <motion.button
                                initial={{ opacity: 0, y: 10 }}
                                animate={{ opacity: 1, y: 0 }}
                                onClick={handleUpload}
                                className="mt-6 w-full py-3 px-6 rounded-lg font-bold uppercase tracking-wider bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-400 hover:to-emerald-500 text-white shadow-lg shadow-green-500/30 transition-all"
                            >
                                Initiate Transmission
                            </motion.button>
                        )}

                    </div>

                    {/* Success Message */}
                    <AnimatePresence>
                        {uploadedFile && (
                            <motion.div
                                initial={{ height: 0, opacity: 0 }}
                                animate={{ height: 'auto', opacity: 1 }}
                                className="mt-6 p-4 bg-green-500/10 border border-green-500/30 rounded-lg flex items-center gap-4"
                            >
                                <FaCheckCircle className="text-green-400 text-2xl" />
                                <div>
                                    <h4 className="font-bold text-green-300">Data Secured</h4>
                                    <p className="text-sm text-green-400/80">Index ID: {uploadedFile._id.substring(0, 10)}...</p>
                                </div>
                            </motion.div>
                        )}
                    </AnimatePresence>
                </div>
            </div>
        </div>
    );
};

export default FileUpload;
