// client/src/pages/Dashboard.jsx

import React, { useContext, useState } from 'react';
import { AuthContext } from '../context/AuthContext';
import BatchUpload from '../components/BatchUpload'; // The new Upload Component
import FileList from '../components/FileList';       // The new Gallery Component
import { motion } from 'framer-motion';
import { FaSignOutAlt, FaRocket } from 'react-icons/fa';

const Dashboard = () => {
    // 1. Get User & Token from Context
    const { user, token, logout } = useContext(AuthContext);

    // 2. State to trigger auto-refresh of the gallery
    const [refreshKey, setRefreshKey] = useState(0);

    // 3. Callback function: Run this when upload is done
    const handleUploadSuccess = () => {
        setRefreshKey(prev => prev + 1); // Changes value -> Triggers useEffect in FileList
    };

    return (
        <div className="min-h-screen bg-slate-900 text-white font-sans selection:bg-cyan-500 selection:text-black">

            {/* 1. Header Section */}
            <motion.header
                initial={{ y: -50, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                className="flex justify-between items-center p-6 border-b border-slate-700 bg-slate-800/50 backdrop-blur-md sticky top-0 z-50 shadow-lg shadow-cyan-500/10"
            >
                <div className="flex items-center gap-3">
                    <FaRocket className="text-cyan-400 text-3xl animate-pulse" />
                    <h1 className="text-2xl font-bold bg-gradient-to-r from-cyan-400 to-blue-500 bg-clip-text text-transparent">
                        AETHER CLOUD
                    </h1>
                </div>

                <div className="flex items-center gap-6">
                    <span className="hidden md:block text-slate-300">
                        Operator: <span className="text-cyan-300 font-mono">{user?.name || user?.username || 'Unknown'}</span>
                    </span>
                    <button
                        onClick={logout}
                        className="flex items-center gap-2 px-4 py-2 bg-red-500/20 text-red-400 border border-red-500/50 rounded-lg hover:bg-red-500 hover:text-white transition-all duration-300 cursor-pointer"
                    >
                        <FaSignOutAlt /> Logout
                    </button>
                </div>
            </motion.header>

            {/* 2. Main Content Area */}
            <main className="max-w-7xl mx-auto p-6 space-y-12">

                {/* Upload Section (Fade In) */}
                <motion.section
                    initial={{ opacity: 0, scale: 0.95 }}
                    animate={{ opacity: 1, scale: 1 }}
                    transition={{ delay: 0.2 }}
                >
                    {/* ðŸ‘‡ Pass Token and Success Handler */}
                    <BatchUpload
                        userToken={token}
                        onUploadSuccess={handleUploadSuccess}
                    />
                </motion.section>

                {/* Divider */}
                <div className="h-px bg-gradient-to-r from-transparent via-slate-600 to-transparent" />

                {/* Gallery Section (Slide Up) */}
                <motion.section
                    initial={{ opacity: 0, y: 50 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: 0.4 }}
                >
                    {/* ðŸ‘‡ Pass Token and Refresh Trigger */}
                    <FileList
                        token={token}
                        refreshTrigger={refreshKey}
                    />
                </motion.section>

            </main>
        </div>
    );
};

export default Dashboard;
