import React, { useEffect, useState } from 'react';
import { getUserFiles, deleteFile } from '../api/files';
import { AnimatePresence, motion } from 'framer-motion'; // ðŸ‘ˆ Import Animation tools
import { 
  FaTrash, FaDownload, FaFileAlt, FaExclamationTriangle, 
  FaImages, FaVideo, FaFilePdf, FaLayerGroup, FaBoxOpen, FaTimes 
} from 'react-icons/fa';

const FileList = ({ token, refreshTrigger }) => { 
  const [files, setFiles] = useState([]);
  const [loading, setLoading] = useState(true);
  const [failedImages, setFailedImages] = useState({}); 
  const [filter, setFilter] = useState('all'); 

  // ðŸ‘‡ NEW STATE: Which image is currently open?
  const [selectedFile, setSelectedFile] = useState(null);

  useEffect(() => {
    const fetchFiles = async () => {
      try {
        const data = await getUserFiles(token);
        setFiles(data);
      } catch (error) {
        console.error("Failed to fetch files", error);
      } finally {
        setLoading(false);
      }
    };

    if (token) fetchFiles();
  }, [token, refreshTrigger]); 

  const handleDelete = async (id) => {
    if(!window.confirm("Are you sure you want to delete this file?")) return;
    try {
      await deleteFile(id, token);
      setFiles(files.filter(file => file._id !== id));
      if (selectedFile?._id === id) setSelectedFile(null); // Close lightbox if deleted
    } catch (error) {
      alert("Failed to delete file");
    }
  };

  const handleImageError = (fileId) => {
    setFailedImages(prev => ({ ...prev, [fileId]: true }));
  };

  const getFileCategory = (mime) => {
    if (mime.startsWith('image/')) return 'image';
    if (mime.startsWith('video/')) return 'video';
    if (mime.includes('pdf') || mime.includes('text') || mime.includes('document')) return 'document';
    return 'other';
  };

  const filteredFiles = files.filter(file => {
    if (filter === 'all') return true;
    return getFileCategory(file.mimetype) === filter;
  });

  if (loading) return <p className="text-center text-slate-400 mt-10 animate-pulse">Loading your gallery...</p>;

  return (
    <div className="mt-10">
      <div className="flex flex-col md:flex-row justify-between items-end mb-6 border-b border-slate-700 pb-2">
        <h3 className="text-2xl font-bold text-white">Your Gallery</h3>
        <div className="flex gap-2 mt-4 md:mt-0 overflow-x-auto pb-1 md:pb-0 w-full md:w-auto">
            {['all', 'image', 'video', 'document', 'other'].map((type) => (
                <button
                    key={type}
                    onClick={() => setFilter(type)}
                    className={`px-4 py-2 rounded-t-lg text-sm font-semibold flex items-center gap-2 transition-all whitespace-nowrap ${
                        filter === type 
                        ? 'bg-cyan-600 text-white shadow-[0_0_15px_rgba(8,145,178,0.4)]' 
                        : 'bg-slate-800 text-slate-400 hover:bg-slate-700 hover:text-white'
                    }`}
                >
                    {type === 'all' && <FaLayerGroup />}
                    {type === 'image' && <FaImages />}
                    {type === 'video' && <FaVideo />}
                    {type === 'document' && <FaFilePdf />}
                    {type === 'other' && <FaBoxOpen />}
                    {type.charAt(0).toUpperCase() + type.slice(1)}
                </button>
            ))}
        </div>
      </div>
      
      {/* GALLERY GRID */}
      {filteredFiles.length === 0 ? (
        <div className="text-center mt-10 p-10 border-2 border-dashed border-slate-700 rounded-xl bg-slate-800/50">
          <p className="text-slate-400 text-lg">No {filter} files found.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
            {filteredFiles.map((file) => {
                const isImage = file.mimetype.startsWith('image/');
                const isBroken = failedImages[file._id];
                const category = getFileCategory(file.mimetype);

                return (
                <div key={file._id} className="bg-slate-800 rounded-xl overflow-hidden shadow-lg border border-slate-700 hover:border-cyan-500 transition-all group relative">
                    <div 
                        className="h-48 bg-slate-900 flex items-center justify-center overflow-hidden relative cursor-pointer"
                        // ðŸ‘‡ CLICK TO OPEN LIGHTBOX
                        onClick={() => isImage && !isBroken ? setSelectedFile(file) : null}
                    >
                    {isImage && !isBroken ? (
                        <img 
                        src={`http://localhost:5000/api/files/${file._id}?t=${new Date(file.uploadDate).getTime()}`} 
                        alt={file.originalName}
                        crossOrigin="anonymous"
                        referrerPolicy="no-referrer"
                        className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                        onError={() => handleImageError(file._id)} 
                        />
                    ) : (
                        <div className="flex flex-col items-center justify-center text-slate-600">
                             {/* Icons logic same as before... */}
                             {isBroken ? <FaExclamationTriangle className="text-4xl text-yellow-500" /> : <FaFileAlt className="text-6xl" />}
                        </div>
                    )}
                    
                    {/* Hover Actions */}
                    <div className="absolute inset-0 bg-black/60 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center gap-4">
                        <button 
                            // Prevent bubbling so clicking button doesn't open lightbox
                            onClick={(e) => { e.stopPropagation(); handleDelete(file._id); }}
                            className="p-3 bg-red-600 rounded-full text-white hover:bg-red-500 transition"
                        >
                            <FaTrash />
                        </button>
                    </div>
                    </div>
                    <div className="p-4 bg-slate-800">
                        <h4 className="text-white font-semibold truncate text-sm">{file.originalName}</h4>
                        <p className="text-slate-400 text-xs mt-1">{(file.size / 1024 / 1024).toFixed(2)} MB</p>
                    </div>
                </div>
                );
            })}
        </div>
      )}

      {/* ðŸ‘‡ PART 2: THE LIGHTBOX OVERLAY */}
      <AnimatePresence>
        {selectedFile && (
          <motion.div 
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 z-50 flex items-center justify-center bg-black/90 backdrop-blur-sm p-4"
            onClick={() => setSelectedFile(null)} // Click outside to close
          >
            {/* Close Button */}
            <button className="absolute top-5 right-5 text-slate-400 hover:text-white text-4xl transition">
              <FaTimes />
            </button>

            {/* Image Container */}
            <motion.div 
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              className="relative max-w-5xl max-h-[90vh] rounded-lg overflow-hidden shadow-2xl"
              onClick={(e) => e.stopPropagation()} // Click image shouldn't close it
            >
              <img 
                src={`http://localhost:5000/api/files/${selectedFile._id}?t=${new Date(selectedFile.uploadDate).getTime()}`}
                alt={selectedFile.originalName}
                crossOrigin="anonymous"
                referrerPolicy="no-referrer"
                className="max-w-full max-h-[85vh] object-contain"
              />
              
              <div className="absolute bottom-0 left-0 right-0 bg-black/70 p-4 text-white flex justify-between items-center backdrop-blur-md">
                <span className="font-semibold">{selectedFile.originalName}</span>
                <a 
                  href={`http://localhost:5000/api/files/${selectedFile._id}`} 
                  target="_blank" 
                  rel="noreferrer"
                  download
                  className="bg-cyan-600 hover:bg-cyan-500 px-4 py-2 rounded text-sm font-bold flex items-center gap-2"
                >
                  <FaDownload /> Download Original
                </a>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default FileList;
