import React, { useEffect, useState } from 'react';
import { getUserFiles, deleteFile } from '../api/files';

const FileList = ({ token, refreshTrigger }) => {
    const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);

    // Fetch files whenever the component loads (or when upload finishes)
    useEffect(() => {
        const fetchFiles = async () => {
            try {
                const data = await getUserFiles(token);
                setFiles(data);
            } catch (error) {
                console.error("Failed to fetch files", error);
            } finally {
                setLoading(false);
            }
        };

        fetchFiles();
    }, [token, refreshTrigger]); // ðŸ‘ˆ Re-runs when 'refreshTrigger' changes

    const handleDelete = async (id) => {
        if (!window.confirm("Are you sure you want to delete this file?")) return;
        try {
            await deleteFile(id, token);
            // Remove from UI instantly without refreshing page
            setFiles(files.filter(file => file._id !== id));
        } catch (error) {
            alert("Failed to delete file");
        }
    };

    if (loading) return <p className="text-center text-gray-400 mt-10">Loading your files...</p>;

    if (files.length === 0) {
        return (
            <div className="text-center mt-10 p-10 border-2 border-dashed border-gray-600 rounded-xl">
                <p className="text-gray-400 text-lg">No files found. Upload something above! ðŸš€</p>
            </div>
        );
    }

    return (
        <div className="mt-10">
            <h3 className="text-2xl font-bold text-white mb-6 border-b border-gray-700 pb-2">Your Gallery</h3>

            {/* THE GRID SYSTEM */}
            <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {files.map((file) => (
                    <div key={file._id} className="bg-gray-800 rounded-xl overflow-hidden shadow-lg border border-gray-700 hover:border-blue-500 transition-all group">

                        {/* 1. THUMBNAIL AREA */}
                        <div className="h-40 bg-gray-900 flex items-center justify-center overflow-hidden relative">
                            {file.mimetype.startsWith('image/') ? (
                                // If it's an image, show it directly from the backend stream
                                <img
                                    src={`http://localhost:5000/api/files/${file._id}`}
                                    alt={file.originalName}
                                    className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                                />
                            ) : (
                                // If not an image, show a generic icon
                                <span className="text-6xl">ðŸ“„</span>
                            )}
                        </div>

                        {/* 2. INFO AREA */}
                        <div className="p-4">
                            <h4 className="text-white font-semibold truncate" title={file.originalName}>
                                {file.originalName}
                            </h4>
                            <p className="text-gray-400 text-xs mt-1">
                                {(file.size / 1024 / 1024).toFixed(2)} MB â€¢ {new Date(file.uploadDate).toLocaleDateString()}
                            </p>

                            {/* 3. ACTION BUTTONS */}
                            <div className="flex justify-between mt-4">
                                {/* Download Button (Direct Link) */}
                                <a
                                    href={`http://localhost:5000/api/files/${file._id}`}
                                    target="_blank"
                                    rel="noreferrer"
                                    className="text-blue-400 hover:text-blue-300 text-sm font-medium"
                                >
                                    Download
                                </a>

                                <button
                                    onClick={() => handleDelete(file._id)}
                                    className="text-red-400 hover:text-red-300 text-sm font-medium"
                                >
                                    Delete
                                </button>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default FileList;
