import React, { useEffect, useState } from 'react';
import { getUserFiles, deleteFile } from '../api/files';
import {
    FaTrash, FaDownload, FaFileAlt, FaExclamationTriangle,
    FaImages, FaVideo, FaFilePdf, FaLayerGroup, FaBoxOpen
} from 'react-icons/fa';

const FileList = ({ token, refreshTrigger }) => {
    const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);
    const [failedImages, setFailedImages] = useState({});

    // Track active filter
    const [filter, setFilter] = useState('all');

    useEffect(() => {
        const fetchFiles = async () => {
            try {
                const data = await getUserFiles(token);
                setFiles(data);
            } catch (error) {
                console.error("Failed to fetch files", error);
            } finally {
                setLoading(false);
            }
        };

        if (token) fetchFiles();
    }, [token, refreshTrigger]);

    const handleDelete = async (id) => {
        if (!window.confirm("Are you sure you want to delete this file?")) return;
        try {
            await deleteFile(id, token);
            setFiles(files.filter(file => file._id !== id));
        } catch (error) {
            alert("Failed to delete file");
        }
    };

    const handleImageError = (fileId) => {
        setFailedImages(prev => ({ ...prev, [fileId]: true }));
    };

    // ðŸ‘‡ HELPER: Determine File Category
    const getFileCategory = (mime) => {
        if (mime.startsWith('image/')) return 'image';
        if (mime.startsWith('video/')) return 'video';
        // Broad check for documents (PDF, Text, Word, Excel, JSON, etc.)
        if (
            mime.includes('pdf') ||
            mime.includes('text') ||
            mime.includes('document') ||
            mime.includes('msword') ||
            mime.includes('sheet') ||
            mime.includes('json')
        ) {
            return 'document';
        }
        // Everything else (Zip, Exe, Unknown)
        return 'other';
    };

    // ðŸ‘‡ FILTER LOGIC: Uses the helper above
    const filteredFiles = files.filter(file => {
        if (filter === 'all') return true;
        return getFileCategory(file.mimetype) === filter;
    });

    if (loading) return <p className="text-center text-slate-400 mt-10 animate-pulse">Loading your gallery...</p>;

    return (
        <div className="mt-10">
            <div className="flex flex-col md:flex-row justify-between items-end mb-6 border-b border-slate-700 pb-2">
                <h3 className="text-2xl font-bold text-white">Your Gallery</h3>

                {/* TAB BAR UI */}
                <div className="flex gap-2 mt-4 md:mt-0 overflow-x-auto pb-1 md:pb-0 w-full md:w-auto">
                    {['all', 'image', 'video', 'document', 'other'].map((type) => (
                        <button
                            key={type}
                            onClick={() => setFilter(type)}
                            className={`px-4 py-2 rounded-t-lg text-sm font-semibold flex items-center gap-2 transition-all whitespace-nowrap ${filter === type
                                    ? 'bg-cyan-600 text-white shadow-[0_0_15px_rgba(8,145,178,0.4)]'
                                    : 'bg-slate-800 text-slate-400 hover:bg-slate-700 hover:text-white'
                                }`}
                        >
                            {type === 'all' && <FaLayerGroup />}
                            {type === 'image' && <FaImages />}
                            {type === 'video' && <FaVideo />}
                            {type === 'document' && <FaFilePdf />}
                            {type === 'other' && <FaBoxOpen />}
                            {type.charAt(0).toUpperCase() + type.slice(1) + (type === 'all' ? '' : 's')}
                        </button>
                    ))}
                </div>
            </div>

            {filteredFiles.length === 0 ? (
                <div className="text-center mt-10 p-10 border-2 border-dashed border-slate-700 rounded-xl bg-slate-800/50">
                    <p className="text-slate-400 text-lg">No {filter} files found.</p>
                </div>
            ) : (
                <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6 animate-fade-in">
                    {filteredFiles.map((file) => {
                        const isImage = file.mimetype.startsWith('image/');
                        const isBroken = failedImages[file._id];
                        const category = getFileCategory(file.mimetype);

                        return (
                            <div key={file._id} className="bg-slate-800 rounded-xl overflow-hidden shadow-lg border border-slate-700 hover:border-cyan-500 transition-all group relative">

                                {/* THUMBNAIL AREA */}
                                <div className="h-48 bg-slate-900 flex items-center justify-center overflow-hidden relative">
                                    {isImage && !isBroken ? (
                                        <img
                                            src={`http://localhost:5000/api/files/${file._id}?t=${new Date(file.uploadDate).getTime()}`}
                                            alt={file.originalName}
                                            crossOrigin="anonymous"
                                            referrerPolicy="no-referrer"
                                            className="w-full h-full object-cover transition-transform duration-500 group-hover:scale-110"
                                            onError={() => handleImageError(file._id)}
                                        />
                                    ) : (
                                        <div className="flex flex-col items-center justify-center text-slate-600 group-hover:text-cyan-400 transition-colors">
                                            {isBroken ? (
                                                <>
                                                    <FaExclamationTriangle className="text-4xl text-yellow-500 mb-2" />
                                                    <span className="text-xs text-yellow-500">Failed</span>
                                                </>
                                            ) : (
                                                // Dynamic Icons based on Category
                                                <>
                                                    {category === 'video' && <FaVideo className="text-6xl" />}
                                                    {category === 'document' && <FaFileAlt className="text-6xl" />}
                                                    {category === 'other' && <FaBoxOpen className="text-6xl" />}
                                                </>
                                            )}
                                        </div>
                                    )}

                                    {/* ACTION BUTTONS (Hover) */}
                                    <div className="absolute inset-0 bg-black/60 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center gap-4">
                                        <a
                                            href={`http://localhost:5000/api/files/${file._id}`}
                                            target="_blank"
                                            rel="noreferrer"
                                            className="p-3 bg-cyan-600 rounded-full text-white hover:bg-cyan-500 transition"
                                            title="Download / View"
                                        >
                                            <FaDownload />
                                        </a>
                                        <button
                                            onClick={() => handleDelete(file._id)}
                                            className="p-3 bg-red-600 rounded-full text-white hover:bg-red-500 transition"
                                            title="Delete"
                                        >
                                            <FaTrash />
                                        </button>
                                    </div>
                                </div>

                                {/* INFO AREA */}
                                <div className="p-4 bg-slate-800">
                                    <h4 className="text-white font-semibold truncate text-sm" title={file.originalName}>
                                        {file.originalName}
                                    </h4>
                                    <p className="text-slate-400 text-xs mt-1 flex justify-between">
                                        <span>{(file.size / 1024 / 1024).toFixed(2)} MB</span>
                                        <span>{new Date(file.uploadDate).toLocaleDateString()}</span>
                                    </p>
                                </div>
                            </div>
                        );
                    })}
                </div>
            )}
        </div>
    );
};

export default FileList;
