import React, { useState, useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { Link } from 'react-router-dom';
import { FaUser, FaEnvelope, FaLock } from 'react-icons/fa';

const Register = () => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const { register } = useContext(AuthContext);

    const handleSubmit = (e) => {
        e.preventDefault();
        register(username, email, password);
    };

    return (
        <div style={styles.container}>
            <div style={styles.card}>
                <h2 style={{ color: '#333' }}>Create Account</h2>
                <form onSubmit={handleSubmit}>
                    <div style={styles.inputGroup}>
                        <FaUser style={styles.icon} />
                        <input type="text" placeholder="Username" value={username} onChange={(e) => setUsername(e.target.value)} style={styles.input} required />
                    </div>
                    <div style={styles.inputGroup}>
                        <FaEnvelope style={styles.icon} />
                        <input type="email" placeholder="Email" value={email} onChange={(e) => setEmail(e.target.value)} style={styles.input} required />
                    </div>
                    <div style={styles.inputGroup}>
                        <FaLock style={styles.icon} />
                        <input type="password" placeholder="Password" value={password} onChange={(e) => setPassword(e.target.value)} style={styles.input} required />
                    </div>
                    <button type="submit" style={styles.button}>Register</button>
                </form>
                <p style={{ marginTop: '15px', fontSize: '14px' }}>
                    Already have an account? <Link to="/login">Login here</Link>
                </p>
            </div>
        </div>
    );
};

const styles = {
    container: { display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh', background: '#f4f6f8' },
    card: { background: 'white', padding: '40px', borderRadius: '10px', boxShadow: '0 4px 6px rgba(0,0,0,0.1)', textAlign: 'center', width: '350px' },
    inputGroup: { display: 'flex', alignItems: 'center', marginBottom: '15px', border: '1px solid #ccc', borderRadius: '5px', padding: '10px' },
    icon: { color: '#888', marginRight: '10px' },
    input: { border: 'none', outline: 'none', width: '100%' },
    button: { width: '100%', padding: '12px', background: '#28a745', color: 'white', border: 'none', borderRadius: '5px', cursor: 'pointer', fontSize: '16px' }
};

export default Register;
