import axios from 'axios';

// Add 'onProgress' as a new parameter
export const uploadFiles = async (formData, token, onProgress) => {
    const config = {
        headers: {
            'Content-Type': 'multipart/form-data',
            Authorization: `Bearer ${token}`,
        },
        // ðŸ‘‡ The Magic Axios Feature
        onUploadProgress: (data) => {
            // Calculate percentage (0 to 100)
            const progress = Math.round((data.loaded / data.total) * 100);

            // Send the number back to your Component
            if (onProgress) {
                onProgress(progress);
            }
        },
    };

    const response = await axios.post('/api/files/upload', formData, config);
    return response.data;
};
