import React, { createContext, useState, useEffect } from 'react';
import axios from 'axios';

export const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    // ðŸ‘‡ FIX: Load token immediately during initialization
    const [token, setToken] = useState(() => localStorage.getItem('token') || null);
    const [user, setUser] = useState(() => {
        const savedUser = localStorage.getItem('user');
        return savedUser ? JSON.parse(savedUser) : null;
    });
    const [loading, setLoading] = useState(false);

    // Optional: Validate token with backend on load
    useEffect(() => {
        if (token) {
            // You could add a /me endpoint call here to verify token validity
            // For now, we assume if it exists, it's valid
        }
    }, [token]);

    const login = async (email, password) => {
        try {
            const res = await axios.post('http://localhost:5000/api/auth/login', { email, password });
            
            // Save to State
            setToken(res.data.token);
            setUser(res.data.user || { name: 'User' }); // Fallback if user object is missing

            // Save to Storage (Persistent)
            localStorage.setItem('token', res.data.token);
            localStorage.setItem('user', JSON.stringify(res.data.user));
            
            return { success: true };
        } catch (error) {
            console.error("Login Error:", error.response?.data?.message);
            return { success: false, error: error.response?.data?.message || 'Login failed' };
        }
    };

    const logout = () => {
        setUser(null);
        setToken(null);
        localStorage.removeItem('token');
        localStorage.removeItem('user');
    };

    return (
        <AuthContext.Provider value={{ user, token, login, logout, loading }}>
            {children}
        </AuthContext.Provider>
    );
};
