import React, { useState } from 'react';
import { uploadFiles } from '../api/files';
import { FaCloudUploadAlt, FaFile, FaCheckCircle, FaExclamationCircle } from 'react-icons/fa';

const BatchUpload = ({ userToken, onUploadSuccess }) => {
    const [files, setFiles] = useState([]);
    const [progress, setProgress] = useState(0);
    const [isUploading, setIsUploading] = useState(false);
    const [status, setStatus] = useState(null); // { type: 'success' | 'error', msg: '' }

    // Handle file selection
    const handleFileChange = (e) => {
        setFiles(e.target.files);
        setStatus(null); // Clear old messages
        setProgress(0);
    };

    // Handle the Upload Logic
    const handleUpload = async () => {
        if (!files || files.length === 0) {
            setStatus({ type: 'error', msg: 'Please select files first.' });
            return;
        }

        const formData = new FormData();
        // Append all selected files to FormData
        for (let i = 0; i < files.length; i++) {
            formData.append('files', files[i]);
        }

        try {
            setIsUploading(true);
            setProgress(0);
            setStatus(null);

            // Call API with Progress Callback
            await uploadFiles(formData, userToken, (percent) => {
                setProgress(percent);
            });

            // Success Handling
            setStatus({ type: 'success', msg: `Successfully uploaded ${files.length} files!` });
            setIsUploading(false);

            // Trigger Dashboard Refresh
            if (onUploadSuccess) onUploadSuccess();

            // Reset Form after delay
            setTimeout(() => {
                setFiles([]);
                setProgress(0);
                setStatus(null);
                // Reset file input value manually if needed
                document.getElementById('fileInput').value = "";
            }, 3000);

        } catch (error) {
            console.error(error);
            setStatus({ type: 'error', msg: 'Upload Failed. Please try again.' });
            setIsUploading(false);
        }
    };

    return (
        <div className="bg-slate-800 rounded-xl border border-slate-700 p-8 shadow-xl max-w-2xl mx-auto relative overflow-hidden">

            {/* Background Glow Effect */}
            <div className="absolute top-0 right-0 w-32 h-32 bg-cyan-500/10 rounded-full blur-3xl -z-10"></div>

            <div className="text-center mb-6">
                <FaCloudUploadAlt className="text-5xl text-cyan-400 mx-auto mb-2 animate-bounce-slow" />
                <h2 className="text-xl font-bold text-white">Upload Files</h2>
                <p className="text-slate-400 text-sm">Supported formats: Images, Videos, Documents</p>
            </div>

            {/* Custom File Input */}
            <div className="mb-6">
                <input
                    id="fileInput"
                    type="file"
                    multiple
                    onChange={handleFileChange}
                    className="block w-full text-sm text-slate-300
                        file:mr-4 file:py-2.5 file:px-6
                        file:rounded-full file:border-0
                        file:text-sm file:font-semibold
                        file:bg-cyan-600 file:text-white
                        hover:file:bg-cyan-500
                        cursor-pointer bg-slate-900/50 rounded-lg border border-slate-600 focus:outline-none focus:border-cyan-500 transition-all"
                />
                {files.length > 0 && (
                    <div className="mt-2 text-xs text-cyan-300 flex items-center gap-1 justify-center">
                        <FaFile /> {files.length} files selected
                    </div>
                )}
            </div>

            {/* Progress Bar */}
            {isUploading && (
                <div className="w-full bg-slate-700 rounded-full h-3 mb-4 overflow-hidden">
                    <div
                        className="bg-gradient-to-r from-cyan-500 to-blue-500 h-3 rounded-full transition-all duration-300 ease-out shadow-[0_0_10px_rgba(6,182,212,0.5)]"
                        style={{ width: `${progress}%` }}
                    ></div>
                    <p className="text-center text-xs text-cyan-200 mt-1 font-mono">{progress}%</p>
                </div>
            )}

            {/* Upload Button */}
            <button
                onClick={handleUpload}
                disabled={isUploading || files.length === 0}
                className={`w-full py-3 rounded-lg font-bold text-white transition-all shadow-lg
                    ${isUploading || files.length === 0
                        ? 'bg-slate-600 cursor-not-allowed text-slate-400'
                        : 'bg-gradient-to-r from-cyan-600 to-blue-600 hover:from-cyan-500 hover:to-blue-500 shadow-cyan-500/20 hover:shadow-cyan-500/40 transform hover:-translate-y-0.5'
                    }`}
            >
                {isUploading ? 'Uploading...' : 'INITIATE UPLOAD'}
            </button>

            {/* Status Messages */}
            {status && (
                <div className={`mt-4 p-3 rounded-lg flex items-center gap-3 text-sm font-medium animate-fade-in
                    ${status.type === 'success'
                        ? 'bg-green-500/10 text-green-400 border border-green-500/20'
                        : 'bg-red-500/10 text-red-400 border border-red-500/20'
                    }`}
                >
                    {status.type === 'success' ? <FaCheckCircle /> : <FaExclamationCircle />}
                    {status.msg}
                </div>
            )}
        </div>
    );
};

export default BatchUpload;
