import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone'; // üëà The magic import
import api from '../services/api';
import { toast } from 'react-toastify';
import { FaCloudUploadAlt, FaCheckCircle, FaSpinner, FaFileCode } from 'react-icons/fa';
import { motion, AnimatePresence } from 'framer-motion';

const FileUpload = () => {
    const [file, setFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [uploadedFile, setUploadedFile] = useState(null);

    // 1. Handle the Drop Action
    const onDrop = useCallback((acceptedFiles) => {
        // We only take the first file for now
        if (acceptedFiles?.length > 0) {
            setFile(acceptedFiles[0]);
            setUploadedFile(null); // Reset success message
        }
    }, []);

    // 2. Configure the Dropzone Area
    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        multiple: false
    });

    const handleUpload = async (e) => {
        e.preventDefault();
        if (!file) return toast.warning("‚ö†Ô∏è No file detected in the buffer.");

        setLoading(true);
        const formData = new FormData();
        formData.append('file', file);

        try {
            const res = await api.post('/files/upload', formData, {
                headers: { 'Content-Type': 'multipart/form-data' },
            });

            toast.success("üöÄ Transmission Complete!");
            setUploadedFile(res.data.file);
            setFile(null); // Clear the dropzone
        } catch (err) {
            console.error(err);
            toast.error("‚ùå Transmission Failed");
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="w-full max-w-2xl mx-auto">
            <div className="relative group">

                {/* Background Glow Effect */}
                <div className={`absolute -inset-1 rounded-2xl blur opacity-25 transition duration-1000 
          ${isDragActive ? 'bg-green-500 opacity-75' : 'bg-gradient-to-r from-cyan-600 to-blue-600 group-hover:opacity-75'}`}>
                </div>

                <div className="relative bg-slate-800 border border-slate-700 p-8 rounded-2xl shadow-2xl">
                    <div className="text-center">

                        {/* üëá DRAG & DROP AREA STARTS HERE üëá */}
                        <div
                            {...getRootProps()}
                            className={`border-2 border-dashed rounded-xl p-10 cursor-pointer transition-all duration-300 flex flex-col items-center justify-center gap-4
                ${isDragActive
                                    ? 'border-green-400 bg-green-500/10 scale-105'
                                    : 'border-slate-600 hover:border-cyan-400 hover:bg-slate-700/50'
                                }
              `}
                        >
                            <input {...getInputProps()} />

                            {/* Dynamic Icon: Changes if you have selected a file or not */}
                            <motion.div
                                animate={isDragActive ? { scale: 1.2 } : { scale: 1 }}
                                className="text-6xl"
                            >
                                {file ? (
                                    <FaFileCode className="text-yellow-400" />
                                ) : (
                                    <FaCloudUploadAlt className={isDragActive ? "text-green-400" : "text-cyan-400"} />
                                )}
                            </motion.div>

                            <div className="space-y-2">
                                {file ? (
                                    // State A: File Selected
                                    <div>
                                        <h3 className="text-xl font-bold text-white">File Locked & Ready</h3>
                                        <p className="text-yellow-400 font-mono text-sm">{file.name}</p>
                                        <p className="text-slate-500 text-xs">{(file.size / 1024).toFixed(2)} KB</p>
                                    </div>
                                ) : (
                                    // State B: Waiting for File
                                    <div>
                                        <h3 className="text-xl font-bold text-white">
                                            {isDragActive ? "Drop to Initialize" : "Upload Data Packet"}
                                        </h3>
                                        <p className="text-slate-400">Drag & drop files here, or click to browse</p>
                                    </div>
                                )}
                            </div>
                        </div>
                        {/* üëÜ DRAG & DROP AREA ENDS HERE üëÜ */}

                        {/* The "Launch" Button - Only shows if a file is ready */}
                        <AnimatePresence>
                            {file && (
                                <motion.button
                                    initial={{ opacity: 0, y: 10 }}
                                    animate={{ opacity: 1, y: 0 }}
                                    exit={{ opacity: 0, y: 10 }}
                                    onClick={handleUpload}
                                    disabled={loading}
                                    className={`mt-6 w-full py-3 px-6 rounded-lg font-bold uppercase tracking-wider transition-all duration-300 
                    ${loading
                                            ? 'bg-slate-700 cursor-not-allowed'
                                            : 'bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-400 hover:to-emerald-500 text-white shadow-lg shadow-green-500/30'
                                        }`}
                                >
                                    {loading ? <span className="flex items-center justify-center gap-2"><FaSpinner className="animate-spin" /> Transmitting...</span> : 'Initiate Transmission'}
                                </motion.button>
                            )}
                        </AnimatePresence>
                    </div>

                    {/* Success Message */}
                    <AnimatePresence>
                        {uploadedFile && (
                            <motion.div
                                initial={{ height: 0, opacity: 0 }}
                                animate={{ height: 'auto', opacity: 1 }}
                                exit={{ height: 0, opacity: 0 }}
                                className="mt-6 p-4 bg-green-500/10 border border-green-500/30 rounded-lg flex items-center gap-4"
                            >
                                <FaCheckCircle className="text-green-400 text-2xl" />
                                <div>
                                    <h4 className="font-bold text-green-300">Data Secured</h4>
                                    <p className="text-sm text-green-400/80">Index ID: {uploadedFile._id.substring(0, 10)}...</p>
                                </div>
                            </motion.div>
                        )}
                    </AnimatePresence>
                </div>
            </div>
        </div>
    );
};

export default FileUpload;
