import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FaFile, FaFileImage, FaFilePdf, FaFileVideo, FaDownload, FaTrash, FaExclamationTriangle } from 'react-icons/fa';

const FileList = ({ token, refreshTrigger }) => {
    const [files, setFiles] = useState([]);
    const [loading, setLoading] = useState(true);

    // Fetch files whenever token or refreshTrigger changes
    useEffect(() => {
        const fetchFiles = async () => {
            try {
                const res = await axios.get('http://localhost:5000/api/files', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setFiles(res.data);
            } catch (err) {
                console.error("Error fetching files:", err);
            } finally {
                setLoading(false);
            }
        };

        if (token) fetchFiles();
    }, [token, refreshTrigger]);

    // Delete file handler
    const deleteFile = async (id) => {
        if (!window.confirm("Are you sure you want to delete this file?")) return;
        try {
            await axios.delete(`http://localhost:5000/api/files/${id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            // Update UI immediately
            setFiles(files.filter(file => file._id !== id));
        } catch (err) {
            alert("Failed to delete file");
        }
    };

    // Helper to determine icon safely
    const getFileIcon = (file) => {
        // Defensive check: If contentType is missing, return Warning Icon
        if (!file?.contentType) return <FaExclamationTriangle className="text-yellow-500 text-4xl" />;

        if (file.contentType.startsWith('image/')) return <FaFileImage className="text-purple-400 text-4xl" />;
        if (file.contentType === 'application/pdf') return <FaFilePdf className="text-red-400 text-4xl" />;
        if (file.contentType.startsWith('video/')) return <FaFileVideo className="text-blue-400 text-4xl" />;

        return <FaFile className="text-gray-400 text-4xl" />;
    };

    if (loading) return <div className="text-white text-center animate-pulse">Loading files...</div>;
    if (files.length === 0) return <div className="text-gray-400 text-center italic">No files uploaded yet. Start by dropping some files above!</div>;

    return (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {files.map(file => (
                <div key={file._id} className="bg-slate-800 p-4 rounded-xl border border-slate-700 hover:border-cyan-500 transition-all group relative overflow-hidden shadow-lg hover:shadow-cyan-500/20">

                    <div className="flex items-center gap-4">
                        <div className="bg-slate-900 p-3 rounded-lg">
                            {getFileIcon(file)}
                        </div>
                        <div className="overflow-hidden">
                            {/* Safe Filename */}
                            <h3 className="font-bold text-white truncate w-40" title={file.filename}>
                                {file.filename || "Unknown File"}
                            </h3>
                            <p className="text-xs text-slate-400">
                                {/* Safe Size Calculation */}
                                {file.length ? (file.length / 1024).toFixed(1) + ' KB' : 'Unknown Size'}
                            </p>
                        </div>
                    </div>

                    {/* Action Buttons */}
                    <div className="mt-4 flex gap-3 opacity-80 group-hover:opacity-100 transition-opacity">
                        <a
                            href={`http://localhost:5000/api/files/${file._id}`}
                            target="_blank"
                            rel="noopener noreferrer"
                            className="flex-1 bg-cyan-600/20 text-cyan-400 py-2 rounded flex items-center justify-center gap-2 hover:bg-cyan-600 hover:text-white transition-colors text-sm font-semibold"
                        >
                            <FaDownload /> Open / Download
                        </a>
                        <button
                            onClick={() => deleteFile(file._id)}
                            className="px-3 bg-red-600/20 text-red-400 rounded hover:bg-red-600 hover:text-white transition-colors"
                        >
                            <FaTrash />
                        </button>
                    </div>

                </div>
            ))}
        </div>
    );
};

export default FileList;
