import React, { useState, useContext, useRef, useEffect } from 'react';
import { AuthContext } from '../context/AuthContext';
import { uploadFiles } from '../api/files';
import { FaUserCircle, FaCamera, FaSave, FaArrowLeft } from 'react-icons/fa';
import { Link, useNavigate } from 'react-router-dom';

const Profile = () => {
    const { user, token, updateUser } = useContext(AuthContext);

    const [name, setName] = useState(user?.name || '');
    const [isUploading, setIsUploading] = useState(false);
    const [message, setMessage] = useState(null);
    const [avatarError, setAvatarError] = useState(false);

    // Local preview for instant feedback
    const [localPreview, setLocalPreview] = useState(null);

    const fileInputRef = useRef(null);

    // Sync name if user data loads late
    useEffect(() => {
        if (user?.name) setName(user.name);
    }, [user]);

    const handleAvatarChange = async (e) => {
        const file = e.target.files[0];
        if (!file) return;

        // 1. Show Instant Preview Immediately
        const objectUrl = URL.createObjectURL(file);
        setLocalPreview(objectUrl);
        setAvatarError(false); // Clear any previous errors

        const formData = new FormData();
        formData.append('files', file);

        try {
            setIsUploading(true);
            setMessage({ type: 'info', text: 'Uploading image...' });

            // 2. Upload to Server
            const res = await uploadFiles(formData, token);

            // Safety Check for ID
            const uploadedFileId = res.files ? res.files[0].gridFsId : res.data?.files[0]?.gridFsId;

            if (!uploadedFileId) throw new Error("Could not find File ID");

            // 3. Update User Profile
            const updateRes = await updateUser(name, uploadedFileId);

            if (updateRes.success) {
                setMessage({ type: 'success', text: 'Avatar updated!' });
                // ðŸ›‘ FIX: We DO NOT clear the local preview here anymore.
                // We let the user keep seeing the image they just picked.
            } else {
                setMessage({ type: 'error', text: 'Failed to save profile.' });
            }
        } catch (error) {
            console.error("Avatar Update Error:", error);
            setMessage({ type: 'error', text: 'Upload failed.' });
            setLocalPreview(null); // Only revert if it ACTUALLY failed
        } finally {
            setIsUploading(false);
        }
    };

    const handleSaveProfile = async (e) => {
        e.preventDefault();
        const res = await updateUser(name, user.avatar);
        if (res.success) {
            setMessage({ type: 'success', text: 'Profile saved!' });
        } else {
            setMessage({ type: 'error', text: res.error });
        }
    };

    // Calculate source: Local Preview gets priority
    const serverAvatarUrl = user?.avatar
        ? `http://localhost:5000/api/files/${user.avatar}`
        : null;

    const displaySrc = localPreview || serverAvatarUrl;

    return (
        <div className="min-h-screen bg-slate-900 text-white flex items-center justify-center p-6">
            <div className="bg-slate-800 p-8 rounded-xl shadow-2xl w-full max-w-lg border border-slate-700 relative">

                <Link to="/" className="absolute top-6 left-6 text-slate-400 hover:text-white transition flex items-center gap-2">
                    <FaArrowLeft /> Back
                </Link>

                <div className="text-center mt-6">
                    <h2 className="text-2xl font-bold">Edit Profile</h2>
                    <p className="text-slate-400 text-sm">Customize your Operator identity</p>
                </div>

                <div className="flex flex-col items-center mt-8">
                    <div className="relative group">
                        <div className="w-32 h-32 rounded-full overflow-hidden border-4 border-slate-700 shadow-xl bg-slate-900 flex items-center justify-center">

                            {displaySrc && !avatarError ? (
                                <img
                                    // ðŸ›‘ FIX: Removed 'key' prop to stop flickering
                                    src={localPreview || `${displaySrc}?t=${new Date().getTime()}`}
                                    alt="Avatar"
                                    className="w-full h-full object-cover"
                                    crossOrigin="anonymous"
                                    onError={(e) => {
                                        // Only show error if we don't have a local preview
                                        if (!localPreview) setAvatarError(true);
                                    }}
                                />
                            ) : (
                                <FaUserCircle className="w-full h-full text-slate-600" />
                            )}
                        </div>

                        <div
                            onClick={() => fileInputRef.current.click()}
                            className="absolute inset-0 bg-black/50 rounded-full flex items-center justify-center opacity-0 group-hover:opacity-100 cursor-pointer transition-all"
                        >
                            <FaCamera className="text-white text-2xl" />
                        </div>
                    </div>

                    <input
                        type="file"
                        ref={fileInputRef}
                        onChange={handleAvatarChange}
                        className="hidden"
                        accept="image/*"
                    />

                    <p className="mt-2 text-xs text-slate-500">Click image to change</p>
                </div>

                <form onSubmit={handleSaveProfile} className="mt-8 space-y-6">
                    <div>
                        <label className="block text-slate-300 text-sm font-bold mb-2">Display Name</label>
                        <input
                            type="text"
                            value={name}
                            onChange={(e) => setName(e.target.value)}
                            className="w-full p-3 bg-slate-900 border border-slate-600 rounded text-white focus:outline-none focus:border-cyan-500 transition-colors"
                        />
                    </div>

                    {message && (
                        <div className={`p-3 rounded text-sm text-center ${message.type === 'error' ? 'bg-red-500/20 text-red-400' : 'bg-green-500/20 text-green-400'}`}>
                            {message.text}
                        </div>
                    )}

                    <button
                        type="submit"
                        disabled={isUploading}
                        className="w-full flex items-center justify-center gap-2 bg-gradient-to-r from-cyan-600 to-blue-600 hover:from-cyan-500 hover:to-blue-500 text-white font-bold py-3 rounded transition-all disabled:opacity-50 shadow-lg shadow-cyan-500/30"
                    >
                        {isUploading ? 'Uploading...' : <><FaSave /> Save Changes</>}
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Profile;
